}

.app_catalog {

    &_text {
        font-size: 18px;
        font-style: normal;
        font-weight: 500;
        line-height: 30px;
        color: $secondary-color;
        margin: 25px 0 90px 0;
    }

    .load_more_btn {
        border-radius: 8px;
        border: 1px solid rgba(255, 255, 255, 0.1);
        background: #3AAFA9;
        color: white;
        font-size: 20px;
        font-style: normal;
        font-weight: 500;
        line-height: normal;
        padding: 17px 38px;
        margin-top: 20px;
