    }

    .app_box {
        width: 1170px;
        display: flex;
        justify-content: space-between;
        flex-wrap: wrap;
        margin-left: 25px;

        .app_cart {
            width: 380px;
            height: 170px;
            padding: 20px;
            border-radius: 15px;


            &:hover {
                background: rgba(40, 48, 54, 0.6);
                backdrop-filter: blur(25px);
            }

            .head {
                display: flex;
                align-items: flex-start;

                .company_icon {
                    max-width: 128px;
                    height: 128px;
                    margin-right: 15px;
                }

                ul {
                    text-align: start;

                    .name {
                        font-size: 24px;
                        font-style: normal;
                        font-weight: 500;
                        line-height: normal;
                        margin-bottom: 3px;
                    }

                    .status {
                        font-size: 18px;
                        font-style: normal;
                        font-weight: 400;
                        line-height: normal;
                        color: $accent-color;
                        text-decoration: line-through #d93f3f;
                    }
                }
            }

            .cart_desc {
                width: 100%;
                max-height: 40%;
                font-size: 24px;
                font-style: normal;
                font-weight: 400;
                line-height: 30px;
                color:  #333333;
                text-align: start;
                word-break: break-all;
                overflow: hidden;
            }

            .control {
                display: flex;
                align-items: center;
                justify-content: space-between;

                .icon {
                    margin-right: 80px;

                    svg {
                        margin-right: 25px;
                        fill: $secondary-color;
                        cursor: pointer;
                    }
                }

                a {
                    font-size: 16px;
                    font-style: normal;
                    font-weight: 600;
                    line-height: normal;
                    padding: 9px 35px;
                }

                .btn {
                    &:hover {
                        border-radius: 8px;
                        background-color: $accent-color;
                    }
                }
            }
        }
    }
}
